mapper.select:
SELECT B. * FROM ( SELECT ROWNUM RN, A. * FROM ( SELECT PRODUCT_NO, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_DESCRIPTION, PRODUCT_IMG, SALES_CNT, PRODUCT_SALE FROM PRODUCT
AND PRODUCT_DESCRIPTION LIKE '%' || #{keyword} || '%'
ORDER BY PRODUCT_NO DESC)A)B WHERE RN >= #{firstIndex} AND RN <= #{lastIndex}

SELECT B. * FROM ( SELECT ROWNUM RN, A. * FROM ( SELECT PR.PRODUCT_NO, PR.PRODUCT_NAME, PR.PRODUCT_PRICE, PR.PRODUCT_DESCRIPTION, PR.PRODUCT_IMG, PR.SALES_CNT, PR.PRODUCT_SALE, PM.PAY_DATE FROM PRODUCT PR JOIN PAYMENT PM ON PR.PRODUCT_NO = PM.PRODUCT_NO
AND PR.PRODUCT_DESCRIPTION LIKE '%' || #{keyword} || '%'
ORDER BY PM.PAY_DATE DESC)A)B WHERE RN >= #{firstIndex} AND RN <= #{lastIndex}

SELECT B. * FROM ( SELECT ROWNUM RN, A. * FROM ( SELECT PR.PRODUCT_NO, PR.PRODUCT_NAME, PR.PRODUCT_PRICE, PR.PRODUCT_DESCRIPTION, PR.PRODUCT_IMG, PR.SALES_CNT, PR.PRODUCT_SALE, OD.ORDER_RDATE FROM PRODUCT PR JOIN ORDERITEM OD ON PR.PRODUCT_NO = OD.PRODUCT_NO
AND PR.PRODUCT_DESCRIPTION LIKE '%' || #{keyword} || '%'
ORDER BY OD.ORDER_RDATE DESC)A)B WHERE RN >= #{firstIndex} AND RN <= #{lastIndex}

SELECT B. * FROM ( SELECT ROWNUM RN, A. * FROM ( SELECT PRODUCT_NO, PRODUCT_NAME, PRODUCT_PRICE, PRODUCT_DESCRIPTION, PRODUCT_IMG, SALES_CNT, PRODUCT_SALE FROM PRODUCT
AND PRODUCT_DESCRIPTION LIKE '%' || #{keyword} || '%'
ORDER BY PRODUCT_NO DESC)A)B WHERE RN >= #{firstIndex} AND RN <= #{lastIndex}

SELECT B. * FROM ( SELECT ROWNUM RN, A. * FROM ( SELECT PR.PRODUCT_NO, PR.PRODUCT_NAME, PR.PRODUCT_PRICE, PR.PRODUCT_DESCRIPTION, PR.PRODUCT_IMG, PR.SALES_CNT, PR.PRODUCT_SALE, CO.CANCEL_DATE FROM PRODUCT PR JOIN CANCELORDER CO ON PR.PRODUCT_NO = CO.PRODUCT_NO
AND PR.PRODUCT_DESCRIPTION LIKE '%' || #{keyword} || '%'
ORDER BY CO.CANCEL_DATE DESC)A)B WHERE RN >= #{firstIndex} AND RN <= #{lastIndex}
